    @limits(calls=SecCallLimit['calls'] / 2, period=SecCallLimit['seconds'])
    def _call_sec(url):
        return requests.get(url)

    def get(self, url):
        return self._call_sec(url).text

def GetSecData(cik, ticker, doc_type, start=0, count=60):
